import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./ProductCard.css";
import Button from "../Buttons/ButtonBuy.jsx";

const ProductCard = ({ product }) => {
    let descriptionData = {};
    try {
        descriptionData = product.description ? JSON.parse(product.description) : {};
    } catch (e) {
        console.error("Description parse error:", e);
    }

    return (
        <div className="col">
            <div className="card h-100 product-card shadow-sm">
                <img
                    src={product?.image}
                    className="card-img-top p-3"
                    alt={product?.name || "Product"}
                    style={{ objectFit: "contain", height: "180px" }}
                />s

                <div className="card-body text-center">
                    <h6 className="card-title fw-bold">{product?.name}</h6>
                    <p className="text-success small mb-1">
                        Còn sẵn: <b>{product?.quantity ?? 0}</b> sản phẩm
                    </p>

                    {/* Giá */}
                    <div className="mb-2">
                        <span className="text-muted text-decoration-line-through me-2">
                            {(product?.price * 1.2).toLocaleString("vi-VN")} ₫
                        </span>
                        <span className="text-warning fw-bold">
                            {product?.price?.toLocaleString("vi-VN") || 0} ₫
                        </span>
                    </div>
                    <div className="card-hover">
                        <ul className="text-start small">
                            {Object.entries(descriptionData).map(([key, value]) => (
                                <li key={key}>
                                    <strong>{key}:</strong> {value}
                                </li>
                            ))}
                        </ul>
                    </div>

                    {/* Nút mua */}
                    <Button
                        text="MUA TÀI KHOẢN"
                        onClick={() => alert("Chức năng đang được phát triển!")}
                    />

                    {/* Hover mô tả */}

                </div>
            </div>
        </div>
    );
};

export default ProductCard;
