// Cấu hình Base URL động dựa trên môi trường
const getBaseURL = () => {
  // Kiểm tra nếu đang chạy trong development
  if (process.env.NODE_ENV === 'development') {
    // Sử dụng environment variable nếu có
    if (process.env.REACT_APP_API_URL) {
      return process.env.REACT_APP_API_URL;
    }
    
    // Tự động phát hiện host hiện tại
    const currentHost = window.location.hostname;
    
    // Nếu truy cập từ localhost hoặc 127.0.0.1
    if (currentHost === 'localhost' || currentHost === '127.0.0.1') {
      return 'http://localhost:8000';
    }
    
    // Nếu truy cập từ IP mạng, sử dụng IP hiện tại
    return `http://192.168.1.67:8000`;
  }
  
  // Production URL
  return process.env.REACT_APP_API_URL || 'http://192.168.1.67:8000';
};

export const BASE_URL = getBaseURL();
