import React from 'react';

const ModalLogin = () => {
    return (
        <div className="modal-dialog modal-lg">
            <div className="modal-login row">
                {/* Left Side */}
                <div className="modal-left col-md-6 d-flex flex-column justify-content-center align-items-center text-white p-4 bg-danger">
                    <h2>Đăng Ký</h2>
                    <p>Bạn chưa có tài khoản? Đăng ký ngay</p>
                    <button className="btn btn-light">Đăng Ký</button>
                </div>

                {/* Right Side */}
                <div className="modal-right col-md-6 p-4">
                    <h2>Đăng Nhập</h2>
                    <form>
                        {/* Tài khoản */}
                        <div className="mb-3">
                            <label htmlFor="account" className="form-label">Tên tài khoản hoặc địa chỉ email</label>
                            <input id="account" type="text" className="form-control" placeholder="Nhập tài khoản hoặc email" />
                        </div>

                        {/* Mật khẩu */}
                        <div className="mb-3">
                            <label htmlFor="password" className="form-label">Mật khẩu *</label>
                            <input id="password" type="password" className="form-control" placeholder="Nhập mật khẩu" />
                        </div>

                        {/* Nhớ mật khẩu */}
                        <div className="form-check mb-3">
                            <input id="remember" type="checkbox" className="form-check-input" />
                            <label htmlFor="remember" className="form-check-label">Nhớ mật khẩu</label>
                        </div>

                        {/* Quên mật khẩu */}
                        <div className="mb-3">
                            <a href="#" className="text-decoration-none">Quên mật khẩu?</a>
                        </div>

                        {/* Nút đăng nhập */}
                        <button type="submit" className="btn btn-outline-danger w-100">Đăng Nhập</button>
                    </form>
                </div>
            </div>

            {/* Footer */}
            <div className="modal-footer d-flex justify-content-center align-items-center">
                <h5>Xin chào mừng bạn đến với Vuavia.vn</h5>
            </div>
        </div>
    );
};

export default ModalLogin;
