import React, { useState, useContext, useEffect } from 'react';
import Logo from '../../Assets/Logo/Logo';
import { FiSearch } from 'react-icons/fi';
import ModalLogin from '../Modal/ModalLogin/ModalLogin';
import { AuthContext } from '../../Context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';
import MenuDropDown from '../Menu/MenuDropDown';

const Header = () => {
    const [showModal, setShowModal] = useState(false);
    const { user, logout } = useContext(AuthContext);
    const [balance, setBalance] = useState(0);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const navigate = useNavigate();

    const handleClickLogo = () => {
        navigate('/');
    };

    const handleLoginClick = () => {
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    useEffect(() => {
        if (user?.balance) {
            setBalance(user.balance);
        } else {
            setBalance(0);
        }
    }, [user]);

    return (
        <>
            <header className="header">
                {/* Logo */}
                <div className="logo-container" onClick={handleClickLogo}>
                    <img src={Logo.ImgLogo} alt="Logo" className="logo" />
                </div>

                {/* Search */}
                <div className="search-container">
                    <input type="text" placeholder="Tìm sản phẩm" />
                    <FiSearch />
                </div>

                {/* Auth */}
                <div className="auth-container">
                    {!user ? (
                        <>
                            <span onClick={handleLoginClick}>Đăng nhập</span>
                            {" / "}
                            <Link to="/register">Đăng ký</Link>
                        </>
                    ) : (
                        <div className="user-menu">
                            <div
                                className="user-info"
                                onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                            >
                                <img
                                    src={user.avatar || "/default-avatar.png"}
                                    alt="avatar"
                                    className="avatar"
                                />
                                <span>{user.name + ' ' + user.name}</span>
                            </div>

                            {isDropdownOpen && (
                                <MenuDropDown logout={logout} navigate={navigate} />
                            )}

                            <div className="balance-container">
                                <button>SỐ DƯ: {balance} đ</button>
                            </div>
                        </div>
                    )}
                </div>
            </header>

            {/* Modal Login */}
            <ModalLogin show={showModal} handleClose={handleCloseModal} />
        </>
    );
};

export default Header;
