import React, { useState, useContext, useEffect } from 'react';
import Logo from '../../Assets/Logo/Logo';
import { FiSearch } from 'react-icons/fi';
import { AuthContext } from '../../Context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';
import MenuDropDown from '../Menu/Menu';
import './Header.css';

const Header = () => {
    const [showModal, setShowModal] = useState(false);
    const { user, logout } = useContext(AuthContext);
    const [balance, setBalance] = useState(0);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        if (user?.balance) setBalance(user.balance);
        else setBalance(0);
    }, [user]);

    const toggleDropdown = () => {
        setIsDropdownOpen((prev) => !prev);
    };

    const closeDropdown = () => {
        setIsDropdownOpen(false);
    };

    return (
        <header className="header">
            {/* Logo */}
            <div className="logo-container" onClick={() => navigate('/')}>
                <img src={Logo.ImgLogo} alt="Logo" className="logo" />
            </div>

            {/* Search */}
            <div className="search-container">
                <input type="text" placeholder="Tìm sản phẩm" className="search-input" />
                <FiSearch className="search-icon" />
            </div>

            {/* Auth */}
            <div className="auth-container">
                {!user ? (
                    <>
                        <span onClick={() => setShowModal(true)}>Đăng nhập</span>
                        {" / "}
                        <Link to="/register">Đăng ký</Link>
                    </>
                ) : (
                    <div className="user-menu">
                        <div className="user-info" onClick={toggleDropdown}>
                            <img
                                src={user.avatar || "/default-avatar.png"}
                                alt="avatar"
                                className="avatar"
                            />
                            <span>{user.surname + ' ' + user.name}</span>
                        </div>

                        {isDropdownOpen && (
                            <MenuDropDown logout={logout} navigate={navigate} closeMenu={closeDropdown} />
                        )}

                        <div className="balance-container">
                            <button className="balance-button">SỐ DƯ: {balance} đ</button>
                        </div>
                    </div>
                )}
            </div>
        </header>
    );
};

export default Header;
