import React from 'react';
import Logo from '../../Assets/Logo/Logo.jsx';

const Header = () => {
    return (
        <header style={styles.header}>
            <div style={styles.logoContainer}>
                <img style={styles.logo} src={Logo.ImgLogo} alt="VUA Logo" />
            </div>
            <div style={styles.searchContainer}>
                <input
                    type="text"
                    style={styles.searchInput}
                    placeholder="Tìm Kiếm sản phẩm"
                />
            </div>
            <div style={styles.authContainer}>
                <button style={styles.loginButton}>Đăng Nhập</button>
                <button style={styles.registerButton}>Đăng Ký</button>
            </div>
            <div style={styles.balanceContainer}>
                <button style={styles.balanceButton}>Số Dư: 0 đ</button>
            </div>
        </header>
    );
};

const styles = {
    header: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '10px 20px',
        backgroundColor: '#f4f4f4',
        borderBottom: '1px solid #ccc',
    },
    logoContainer: {
        flex: '1',
    },
    logo: {
        height: '50px',
    },
    searchContainer: {
        flex: '2',
        display: 'flex',
        justifyContent: 'center',
    },
    searchInput: {
        width: '80%',
        padding: '8px',
        fontSize: '16px',
        borderRadius: '4px',
        border: '1px solid #ccc',
    },
    authContainer: {
        flex: '1',
        display: 'flex',
        justifyContent: 'space-around',
    },
    loginButton: {
        padding: '8px 12px',
        backgroundColor: '#007bff',
        color: '#fff',
        border: 'none',
        borderRadius: '4px',
    },
    registerButton: {
        padding: '8px 12px',
        backgroundColor: '#28a745',
        color: '#fff',
        border: 'none',
        borderRadius: '4px',
    },
    balanceContainer: {
        flex: '1',
        display: 'flex',
        justifyContent: 'flex-end',
    },
    balanceButton: {
        padding: '8px 12px',
        backgroundColor: '#ffc107',
        color: '#000',
        border: 'none',
        borderRadius: '4px',
    },
};

export default Header;
