import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getTypeProductById } from "../../Services/ApiTypeProduct.jsx";

const DetailPageTypeProduct = () => {
    const { typeProductId } = useParams();
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        (async () => {
            try {
                setLoading(true);
                const res = await getTypeProductById(typeProductId);
                setProduct(res);
            } catch (e) {
                setError("Không thể tải chi tiết sản phẩm.");
            } finally {
                setLoading(false);
            }
        })();
    }, [typeProductId]);

    if (loading) return <div>Đang tải...</div>;
    if (error) return <div>{error}</div>;
    if (!product) return null;

    return (
        <div>
            <h2>{product.name}</h2>
            <img src={product.image} alt={product.name} style={{ maxWidth: 300 }} />
            <p>Giá: {product.price?.toLocaleString("vi-VN")} ₫</p>
            <p>Còn lại: {product.quantity}</p>
            <div>
                <h4>Mô tả:</h4>
                <pre>{JSON.stringify(product.description, null, 2)}</pre>
            </div>
        </div>
    );
};

export default DetailPageTypeProduct;
