import React, { useState, useContext, useEffect } from "react";
import { Navbar, Nav, NavDropdown } from "react-bootstrap";
import { FiSearch } from "react-icons/fi";
import { AuthContext } from "../../Context/AuthContext";
import { useNavigate, Link } from "react-router-dom";
import Logo from "../../Assets/Logo/Logo";
import DefaultAvt from "../../Assets/Logo/Logo";
import "./Header.css";

const Header = () => {
    const { user, logout } = useContext(AuthContext);
    const [balance, setBalance] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        setBalance(user?.balance || 0);
    }, [user]);

    return (
        <Navbar expand="lg" className="header">
            {/* Logo */}
            <div className="logo-container">
                <img
                    src={Logo.ImgLogo}
                    alt="logo"
                    className="logo"
                    onClick={() => navigate("/")}
                />
            </div>

            {/* Search */}
            <div className="search-container">
                <input
                    type="text"
                    className="search-input"
                    placeholder="Tìm sản phẩm"
                />
                <FiSearch className="search-icon" />
            </div>

            {/* Auth */}
            <div className="auth-container">
                {!user ? (
                    <span onClick={() => navigate("/login")}>Đăng nhập / Đăng ký</span>
                ) : (
                    <div className="user-menu">
                        <NavDropdown
                            title={
                                <span className="user-info">
                                    <img
                                        src={user.avatar || DefaultAvt}
                                        alt="avatar"
                                        className="avatar"
                                    />
                                    {user.surname + " " + user.name}
                                </span>
                            }
                            align="end"
                            className="dropdown"
                        >
                            <NavDropdown.Item as={Link} to="/account">
                                Trang Tài Khoản
                            </NavDropdown.Item>
                            <NavDropdown.Item as={Link} to="/my-orders">
                                Đơn Hàng Của Tôi
                            </NavDropdown.Item>
                            <NavDropdown.Item as={Link} to="/recharge">
                                Nạp Tiền
                            </NavDropdown.Item>
                            <NavDropdown.Item as={Link} to="/transactions">
                                Lịch Sử Giao Dịch
                            </NavDropdown.Item>
                            <NavDropdown.Item onClick={() => logout()}>
                                Đăng Xuất
                            </NavDropdown.Item>
                        </NavDropdown>

                        <div className="balance-container">
                            <button className="balance-button">SỐ DƯ: {balance} đ</button>
                        </div>
                    </div>
                )}
            </div>
        </Navbar>
    );
};

export default Header;
