import React, { useState, useEffect, createContext } from "react";
import AuthService from "../Services/AuthService";
import UserService, { getProfile } from "../Services/UserService";
export const AuthContext = createContext();
const AuthProvider = ({ element }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [token, setToken] = useState(localStorage.getItem("token"));

    useEffect(() => {
        if (token) {
            getProfile().then(setUser).catch(() => setUser(null))

        }
    }, [token]);
    const login = async (credentials) => {
        setLoading(true);
        try {
            const data = await AuthService.login(credentials);
            localStorage.setItem("access_token", data.access_token);
            localStorage.setItem("refresh_token", data.refresh_token);
            setToken(data.access_token);
        } catch (error) {
            console.error("Login failed:", error);
        } finally {
            setLoading(false);
        }
    };
    const logout = async () => {
        await AuthService.logout();
        localStorage.removeItem("access_token");
        localStorage.removeItem("refresh_token");
        setToken(null);
        setUser(null);
    };
    const register = async (userInfo) => {
        setLoading(true);
        try {
            await AuthService.register(userInfo);
            localStorage.setItem("access_token", userInfo.access_token);
            localStorage.setItem("refresh_token", userInfo.refresh_token);
            setToken(userInfo.access_token);
        } catch (error) {
            console.error("Registration failed:", error);
        } finally {
            setLoading(false);
        }
    };
    return (
        <AuthContext.Provider value={{ user, loading, login, logout, register }}>
            {element}
        </AuthContext.Provider>
    );

};

export default AuthProvider;
