import React, { useEffect, useMemo, useState } from "react";
import { useParams } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import SpecList from "../../Components/Product/SpecList";
import QuantityInput from "../../Components/Product/QuantityInput";
import Button from "../../Components/Buttons/ButtonBuy";
import {
    getTypeProductById,
    getinfortypeproduct,
} from "../../Services/ApiTypeProduct";

// ----- helpers -----
const parseDescription = (desc) => {
    if (!desc) return {};
    if (typeof desc === "object") return desc;
    try {
        return JSON.parse(desc);
    } catch {
        return {};
    }
};

const formatPrice = (n) =>
    (Number(n) || 0).toLocaleString("vi-VN", { maximumFractionDigits: 0 });

/** Chuẩn hoá key vì API có thể trả "Ctl" hoặc "Ctl " (thừa space) */
const normalizeDescribe = (raw) => {
    if (!raw) return null;
    const get = (k) => raw?.[k] ?? raw?.[`${k} `] ?? raw?.[k?.toLowerCase()];
    return {
        ctl: get("Ctl"),
        question: get("Cau Hoi") ?? get("Question"),
    };
};

const TypeProductDetail = () => {
    const { id } = useParams(); // route: /detail-type-product/:id

    const [product, setProduct] = useState(null);
    const [describe, setDescribe] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [qty, setQty] = useState(1);
    const [activeTab, setActiveTab] = useState("desc"); // 'desc' | 'review'

    // fetch product + extra describe info
    useEffect(() => {
        let mounted = true;
        (async () => {
            try {
                setLoading(true);
                console.log("Fetching data for ID:", id); // Debug ID
                
                // Fetch product first (critical)
                const pRes = await getTypeProductById(id);
                console.log("Product data:", pRes); // Debug product response
                if (!mounted) return;
                setProduct(pRes);
                
                // Fetch describe (optional, handle error separately)
                try {
                    const infoRes = await getinfortypeproduct(id);
                    console.log("Info data:", infoRes); // Debug info response
                    if (mounted) setDescribe(normalizeDescribe(infoRes?.describe));
                } catch (infoError) {
                    console.warn("Failed to fetch describe info:", infoError); // Warn instead of error
                    // Still proceed without describe
                }
            } catch (e) {
                console.error("Error fetching product:", e); // Detailed error log
                if (mounted) setError("Không thể tải chi tiết sản phẩm.");
            } finally {
                if (mounted) setLoading(false);
            }
        })();
        return () => {
            mounted = false;
        };
    }, [id]);

    const specs = useMemo(() => parseDescription(product?.description), [product]);

    const handleBuy = () => {
        alert(`Mua ${qty} × "${product?.name}" (ID: ${product?.id})`);
        // TODO: mở modal / gọi API tạo đơn
    };

    if (loading) {
        return (
            <div className="container py-5 text-center">
                <div className="spinner-border" role="status" />
                <p className="mt-2">Đang tải chi tiết…</p>
            </div>
        );
    }
    if (error) {
        return (
            <div className="container py-5">
                <div className="alert alert-danger text-center">{error}</div>
            </div>
        );
    }
    if (!product) return null;

    return (
        <div className="container py-4">
            {/* breadcrumb đơn giản */}
            <nav className="mb-3 small text-muted">
                <span>TRANG CHỦ</span> / <span>SHOP</span>
            </nav>

            {/* ===== HÀNG CHI TIẾT SẢN PHẨM ===== */}
            <div className="row g-4">
                {/* Ảnh */}
                <div className="col-lg-6">
                    <div className="p-3 border rounded-3 bg-white h-100 d-flex align-items-center justify-content-center">
                        <img
                            src={product.image}
                            alt={product.name}
                            className="img-fluid"
                            style={{ maxHeight: 480, objectFit: "contain" }}
                        />
                    </div>
                </div>

                {/* Thông tin */}
                <div className="col-lg-6">
                    <h3 className="fw-bold">{product.name}</h3>
                    <div className="text-success fw-semibold mb-2">
                        Còn sẵn: {product.quantity ?? 0} sản phẩm
                    </div>

                    {/* Giá + giá gốc mock */}
                    <div className="d-flex align-items-baseline gap-3 mb-3">
                        <span className="fs-3 fw-bold text-danger">
                            {formatPrice(product.price)} đ
                        </span>
                        <del className="text-muted">{formatPrice(product.price * 2)} đ</del>
                    </div>

                    {/* Thông số ngắn (từ product.description JSON) */}
                    <SpecList specs={specs} />

                    {/* Số lượng + mua */}
                    <div className="d-flex align-items-center gap-3 my-3">
                        <QuantityInput
                            value={qty}
                            min={1}
                            max={product.quantity ?? 1}
                            onChange={setQty}
                        />
                        <Button text="MUA TÀI KHOẢN" onClick={handleBuy} />
                    </div>

                    {/* Mã sản phẩm */}
                    <div className="mt-3 small text-muted">
                        <div>Mã: HMT{String(product.id).padStart(2, "0")}</div>
                    </div>
                </div>
            </div>

            {/* ===== HỘP MÔ TẢ Ở DƯỚI CHI TIẾT ===== */}
            <div className="mt-5">
                {/* Tabs (Bootstrap) */}
                <ul className="nav nav-tabs">
                    <li className="nav-item">
                        <button
                            className={`nav-link ${activeTab === "desc" ? "active" : ""}`}
                            onClick={() => setActiveTab("desc")}
                            type="button"
                        >
                            Mô tả
                        </button>
                    </li>
                    <li className="nav-item">
                        <button
                            className={`nav-link ${activeTab === "review" ? "active" : ""}`}
                            onClick={() => setActiveTab("review")}
                            type="button"
                        >
                            Đánh giá (0)
                        </button>
                    </li>
                </ul>

                {/* Nội dung tab */}
                <div className="border border-top-0 rounded-bottom p-4 bg-white">
                    {activeTab === "desc" && (
                        <div>
                            <h4 className="fw-bold mb-3">
                                {describe?.question || "Mô tả chi tiết"}
                            </h4>
                            {describe?.ctl ? (
                                <div style={{ lineHeight: 1.7, whiteSpace: "pre-line" }}>
                                    {describe.ctl}
                                </div>
                            ) : (
                                <p className="text-muted fst-italic mb-0">
                                    Chưa có mô tả chi tiết.
                                </p>
                            )}
                        </div>
                    )}

                    {activeTab === "review" && (
                        <div className="text-muted">
                            Chưa có đánh giá nào. Hãy là người đầu tiên đánh giá sản phẩm này!
                            {/* TODO: chèn form/DS đánh giá tại đây */}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default TypeProductDetail;
