import React from 'react';
import './HomePage.css';
import Logo from '../../Assets/Logo/Logo.jsx';

const HomePage = () => {
    const latestPosts = [
        {
            id: 1,
            title: "Hướng Dẫn Cài Proxy Cho Telegram Miễn Phí - Truy Cập Nhanh, Bỏ Chặn Dễ Dàng",
            image: Logo.share1
        },
        {
            id: 2,
            title: "Các tips và chiến lược để tối ưu hóa việc đăng bài Facebook theo khung giờ.",
            image: Logo.share2
        },
        {
            id: 3,
            title: "Mẹo để sử dụng Facebook Lead Ads thu thập thông tin khách hàng tiềm năng",
            image: Logo.share3
        },
        {
            id: 4,
            title: "Mẹo tạo quảng cáo story trên facebook hiệu quả và thu hút người xem",
            image: Logo.share4
        }
    ];

    return (
        <div className="homepage">
            <div className="container-fluid">
                <div className="row">
                    {/* Main Content */}
                    <div className="col-lg-9 col-md-8">
                        {/* Hero Banner */}
                        <div className="hero-banner mb-4">
                            <div className="banner-content">
                                <div className="banner-left">
                                    <div className="vuavia-logo">
                                        <img src={Logo.ImgLogo} alt="Vuavia Logo" className="img-fluid" />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>



                    {/* Sidebar */}
                    <div className="col-lg-3 col-md-4">
                        <div className="sidebar">
                            <div className="sidebar-header">
                                <h4 className="sidebar-title">
                                    <i className="fas fa-chevron-down"></i>
                                    CHIA SẺ MỚI NHẤT !
                                </h4>
                            </div>
                            <div className="sidebar-content">
                                {latestPosts.map((post) => (
                                    <div key={post.id} className="sidebar-post">
                                        <div className="post-image">
                                            <img src="/assets/post-thumbnail.jpg" alt={post.title} className="img-fluid" />
                                        </div>
                                        <div className="post-content">
                                            <h6 className="post-title">{post.title}</h6>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default HomePage;
