// src/Pages/TypeProductDetail/TypeProductDetail.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useParams } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import SpecList from "../../Components/Product/SpecList";
import QuantityInput from "../../Components/Product/QuantityInput";
import Button from "../../Components/Buttons/ButtonBuy";
import { getTypeProductById, getinfortypeproduct } from "../../Services/ApiTypeProduct";

const TypeProductDetail = () => {
    const { id } = useParams();

    const [product, setProduct] = useState(null);
    const [infor, setinfor] = useState(null);
    const [qty, setQty] = useState(1); // Thêm state cho số lượng
    const [activeTab, setActiveTab] = useState("desc"); // Thêm state cho tab active

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const data = await getTypeProductById(id);
                const infor = await getinfortypeproduct(id);
                setProduct(data);
                setinfor(infor);
            } catch (error) {
                console.error("Failed to fetch product:", error);
            }
        };
        fetchProduct();
    }, [id]);

    const descriptionData = product?.description || {};
    const describe = infor || {};

    // Hàm format giá
    const formatPrice = (price) => {
        return new Intl.NumberFormat('vi-VN').format(price);
    };

    // Hàm xử lý mua hàng
    const handleBuy = () => {
        // Logic mua hàng, ví dụ: thêm vào giỏ hàng hoặc chuyển đến checkout
        alert(`Đã mua ${qty} sản phẩm: ${product?.name}`);
        // Bạn có thể tích hợp với API mua hàng ở đây
    };

    if (!product) {
        return <div className="container py-4">Đang tải...</div>;
    }

    return (
        <div className="container py-4">
            {/* breadcrumb đơn giản */}
            <nav className="mb-3 small text-muted">
                <span>TRANG CHỦ</span> / <span>SHOP</span>
            </nav>

            {/* ===== HÀNG CHI TIẾT SẢN PHẨM ===== */}
            <div className="row g-4">
                {/* Ảnh */}
                <div className="col-lg-6">
                    <div className="p-3 border rounded-3 bg-white h-100 d-flex align-items-center justify-content-center">
                        <img
                            src={product.image}
                            alt={product.name}
                            className="img-fluid"
                            style={{ maxHeight: 480, objectFit: "contain" }}
                        />
                    </div>
                </div>

                {/* Thông tin */}
                <div className="col-lg-6">
                    <h3 className="fw-bold">{product.name}</h3>
                    <div className="text-success fw-semibold mb-2">
                        Còn sẵn: {product.quantity ?? 0} sản phẩm
                    </div>

                    {/* Giá + giá gốc mock */}
                    <div className="d-flex align-items-baseline gap-3 mb-3">
                        <span className="fs-3 fw-bold text-danger">
                            {formatPrice(product.price)} đ
                        </span>
                        <del className="text-muted">{formatPrice(product.price * 2)} đ</del>
                    </div>

                    {/* Số lượng + mua */}
                    <div className="d-flex align-items-center gap-3 my-3">
                        <QuantityInput
                            value={qty}
                            min={1}
                            max={product.quantity ?? 1}
                            onChange={setQty}
                        />
                        <Button text="MUA TÀI KHOẢN" onClick={handleBuy} />
                    </div>

                    {/* Mã sản phẩm */}
                    <div className="mt-3 small text-muted">
                        <div>Mã: HMT{String(product.id).padStart(2, "0")}</div>
                    </div>
                </div>
            </div>

            {/* ===== HỘP MÔ TẢ Ở DƯỚI CHI TIẾT ===== */}
            <div className="mt-5">
                {/* Tabs (Bootstrap) */}
                <ul className="nav nav-tabs">
                    <li className="nav-item">
                        <button
                            className={`nav-link ${activeTab === "desc" ? "active" : ""}`}
                            onClick={() => setActiveTab("desc")}
                            type="button"
                        >
                            Mô tả
                        </button>
                    </li>
                    <li className="nav-item">
                        <button
                            className={`nav-link ${activeTab === "review" ? "active" : ""}`}
                            onClick={() => setActiveTab("review")}
                            type="button"
                        >
                            Đánh giá (0)
                        </button>
                    </li>
                </ul>

                {/* Nội dung tab */}
                <div className="border border-top-0 rounded-bottom p-4 bg-white">
                    {activeTab === "desc" && (
                        <div>
                            <h4 className="fw-bold mb-3">
                                {describe?.question || "Mô tả chi tiết"}
                            </h4>
                            {describe?.ctl ? (
                                <div style={{ lineHeight: 1.7, whiteSpace: "pre-line" }}>
                                    {describe.ctl}
                                </div>
                            ) : (
                                <p className="text-muted fst-italic mb-0">
                                    Chưa có mô tả chi tiết.
                                </p>
                            )}
                        </div>
                    )}

                    {activeTab === "review" && (
                        <div className="text-muted">
                            Chưa có đánh giá nào. Hãy là người đầu tiên đánh giá sản phẩm này!
                            {/* TODO: chèn form/DS đánh giá tại đây */}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default TypeProductDetail;
