import React, { useState, useContext, useEffect } from 'react';
import Logo from '../../Assets/Logo/Logo';
import { FiSearch } from 'react-icons/fi';
import './Header.css';
import ModalLogin from '../Modal/ModalLogin/ModalLogin';
import { AuthContext } from '../../Context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';
import DefaultAvt from '../../Assets/Logo/Logo';
import MenuDropDown from '../Menu/Menu';

const Header = () => {
    const [showModal, setShowModal] = useState(false);
    const { user, logout } = useContext(AuthContext);
    const [balance, setBalance] = useState(0);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const navigate = useNavigate();

    const handleClickLogo = () => {
        navigate('/');
    };

    const handleLoginClick = () => {
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    useEffect(() => {
        if (user?.balance) {
            setBalance(user.balance);
        } else {
            setBalance(0);
        }
    }, [user]);

    const handleShowDropdown = () => {
        setIsDropdownOpen(true);
    };

    const handleHideDropdown = () => {
        setIsDropdownOpen(false);
    };

    return (
        <>
            <header className="header">
                {/* Logo */}
                <div className="logo-container">
                    <img
                        src={Logo.ImgLogo}
                        alt="VUA Logo"
                        className="logo"
                        onClick={handleClickLogo}
                    />
                </div>

                {/* Search */}
                <div className="search-container">
                    <input
                        type="text"
                        className="search-input"
                        placeholder="Tìm sản phẩm"
                    />
                    <FiSearch className="search-icon" />
                </div>

                {/* Auth */}
                <div className="auth-container">
                    {!user ? (
                        <>
                            <span onClick={handleLoginClick}>Đăng nhập</span>
                            {" / "}
                            <Link to="/register">Đăng ký</Link>
                        </>
                    ) : (
                        <div
                            className="user-menu"
                            onMouseEnter={handleShowDropdown}
                            onMouseLeave={handleHideDropdown}
                        >
                            <img
                                src={user.avatar || "/default-avatar.png"}
                                alt="avatar"
                                className="avatar"
                            />
                            <span>{user.username}</span>
                            <button onClick={logout} color='yellow'>Đăng xuất</button>
                            <div className="balance-container">
                                <button className="balance-button">
                                    SỐ DƯ: {balance} đ
                                </button>
                            </div>
                            {isDropdownOpen && (
                                <div className="dropdown-menu">
                                    <Link to="/profile">Trang cá nhân</Link>
                                    <button onClick={logout}>Đăng xuất</button>
                                </div>
                            )}
                        </div>
                    )}
                </div>
            </header>

            {/* Modal Login */}
            <ModalLogin show={showModal} handleClose={handleCloseModal} />
        </>
    );
};

export default Header;
