import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./ProductCard.css";

const ProductCard = ({ product }) => {
    // Parse description JSON
    let descriptionData = {};
    try {
        descriptionData = JSON.parse(product.description);
    } catch (e) {
        console.error("Description parse error:", e);
    }

    return (
        <div className="col">
            <div className="card h-100 product-card shadow-sm">
                {/* Ảnh */}
                <img
                    src={product.image}
                    className="card-img-top p-3"
                    alt={product.name}
                    style={{ objectFit: "contain", height: "180px" }}
                />

                {/* Nội dung */}
                <div className="card-body text-center">
                    <h6 className="card-title fw-bold">{product.name}</h6>
                    <p className="text-success small mb-1">
                        Còn sẵn: <b>{product.quantity}</b> sản phẩm
                    </p>
                    <h6 className="text-warning fw-bold">
                        {product.price.toLocaleString("vi-VN")} ₫
                    </h6>
                    <button className="btn btn-outline-warning btn-sm fw-bold mt-2">
                        MUA TÀI KHOẢN
                    </button>
                </div>

                {/* Hover overlay */}
                <div className="card-hover">
                    <ul className="text-start small">
                        {Object.entries(descriptionData).map(([key, value]) => (
                            <li key={key}>
                                <strong>{key}:</strong> {value}
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default ProductCard;
