// src/Pages/TypeProductDetail/TypeProductDetail.jsx
import React, { useEffect, useMemo, useState } from "react";
import { useParams } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import SpecList from "../../Components/Product/SpecList";
import QuantityInput from "../../Components/Product/QuantityInput";
import Button from "../../Components/Buttons/ButtonBuy";
import { getTypeProductById, getinfortypeproduct } from "../../Services/ApiTypeProduct";

const TypeProductDetail = () => {
    const { id } = useParams();

    const [product, setProduct] = useState(null);
    const [infor, setinfor] = useState(null);
    const [qty, setQty] = useState(1);
    const [activeTab, setActiveTab] = useState("desc");
    const [loading, setLoading] = useState(true); // Thêm loading state
    const [error, setError] = useState(null); // Thêm error state

    useEffect(() => {
        const fetchProduct = async () => {
            setLoading(true);
            setError(null);
            
            try {
                console.log("Fetching product with ID:", id); // Debug log
                
                const data = await getTypeProductById(id); // Sử dụng id từ params thay vì hard-code 4
                const infor = await getinfortypeproduct(id); // Sử dụng id từ params
                
                console.log("Product data:", data); // Debug log
                console.log("Infor data:", infor); // Debug log
                
                setProduct(data);
                setinfor(infor);
            } catch (error) {
                console.error("Failed to fetch product:", error);
                setError(error.message || "Có lỗi xảy ra khi tải sản phẩm");
            } finally {
                setLoading(false);
            }
        };
        
        if (id) { // Chỉ fetch khi có ID
            fetchProduct();
        }
    }, [id]);

    // Thêm xử lý loading
    if (loading) {
        return (
            <div className="container py-4">
                <div className="text-center">
                    <div className="spinner-border" role="status">
                        <span className="visually-hidden">Đang tải...</span>
                    </div>
                    <p className="mt-2">Đang tải thông tin sản phẩm...</p>
                </div>
            </div>
        );
    }

    // Thêm xử lý error
    if (error) {
        return (
            <div className="container py-4">
                <div className="alert alert-danger" role="alert">
                    <h4 className="alert-heading">Lỗi!</h4>
                    <p>{error}</p>
                    <button 
                        className="btn btn-outline-danger" 
                        onClick={() => window.location.reload()}
                    >
                        Thử lại
                    </button>
                </div>
            </div>
        );
    }

    // Kiểm tra xem có sản phẩm không
    if (!product) {
        return (
            <div className="container py-4">
                <div className="alert alert-warning" role="alert">
                    Không tìm thấy sản phẩm với ID: {id}
                </div>
            </div>
        );
    }

    const descriptionData = product?.description || {};
    const describe = infor || {};

    // Hàm format giá
    const formatPrice = (price) => {
        return new Intl.NumberFormat('vi-VN').format(price);
    };

    // Hàm xử lý mua hàng
    const handleBuy = () => {
        // Logic mua hàng, ví dụ: thêm vào giỏ hàng hoặc chuyển đến checkout
        alert(`Đã mua ${qty} sản phẩm: ${product?.name}`);
        // Bạn có thể tích hợp với API mua hàng ở đây
    };

    return (
        <div className="container py-4">
            {/* breadcrumb đơn giản */}
            <nav className="mb-3 small text-muted">
                <span>TRANG CHỦ</span> / <span>SHOP</span>
            </nav>

            {/* ===== HÀNG CHI TIẾT SẢN PHẨM ===== */}
            <div className="row g-4">
                {/* Ảnh */}
                <div className="col-lg-6">
                    <div className="p-3 border rounded-3 bg-white h-100 d-flex align-items-center justify-content-center">
                        <img
                            src={product.image}
                            alt={product.name}
                            className="img-fluid"
                            style={{ maxHeight: 480, objectFit: "contain" }}
                        />
                    </div>
                </div>

                {/* Thông tin */}
                <div className="col-lg-6">
                    <h3 className="fw-bold">{product.name}</h3>
                    <div className="text-success fw-semibold mb-2">
                        Còn sẵn: {product.quantity ?? 0} sản phẩm
                    </div>

                    {/* Giá + giá gốc mock */}
                    <div className="d-flex align-items-baseline gap-3 mb-3">
                        <span className="fs-3 fw-bold text-danger">
                            {formatPrice(product.price)} đ
                        </span>
                        <del className="text-muted">{formatPrice(product.price * 2)} đ</del>
                    </div>

                    {/* Số lượng + mua */}
                    <div className="d-flex align-items-center gap-3 my-3">
                        <QuantityInput
                            value={qty}
                            min={1}
                            max={product.quantity ?? 1}
                            onChange={setQty}
                        />
                        <Button text="MUA TÀI KHOẢN" onClick={handleBuy} />
                    </div>

                    {/* Mã sản phẩm */}
                    <div className="mt-3 small text-muted">
                        <div>Mã: HMT{String(product.id).padStart(2, "0")}</div>
                    </div>
                </div>
            </div>

            {/* ===== HỘP MÔ TẢ Ở DƯỚI CHI TIẾT ===== */}
            <div className="mt-5">
                {/* Tabs (Bootstrap) */}
                <ul className="nav nav-tabs">
                    <li className="nav-item">
                        <button
                            className={`nav-link ${activeTab === "desc" ? "active" : ""}`}
                            onClick={() => setActiveTab("desc")}
                            type="button"
                        >
                            Mô tả
                        </button>
                    </li>
                    <li className="nav-item">
                        <button
                            className={`nav-link ${activeTab === "review" ? "active" : ""}`}
                            onClick={() => setActiveTab("review")}
                            type="button"
                        >
                            Đánh giá (0)
                        </button>
                    </li>
                </ul>

                {/* Nội dung tab */}
                <div className="border border-top-0 rounded-bottom p-4 bg-white">
                    {activeTab === "desc" && (
                        <div>
                            <h4 className="fw-bold mb-3">
                                {describe?.question || "Mô tả chi tiết"}
                            </h4>
                            {describe?.ctl ? (
                                <div style={{ lineHeight: 1.7, whiteSpace: "pre-line" }}>
                                    {describe.ctl}
                                </div>
                            ) : (
                                <p className="text-muted fst-italic mb-0">
                                    Chưa có mô tả chi tiết.
                                </p>
                            )}
                        </div>
                    )}

                    {activeTab === "review" && (
                        <div className="text-muted">
                            Chưa có đánh giá nào. Hãy là người đầu tiên đánh giá sản phẩm này!
                            {/* TODO: chèn form/DS đánh giá tại đây */}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default TypeProductDetail;
