import React, { useState } from "react";
import Button from "../Buttons/ButtonBuy.jsx";

const ProductCard = ({ product, showTooltipOnHover = false }) => {
    const [showTooltip, setShowTooltip] = useState(false);

    // Parse description JSON string -> object
    let desc = {};
    try {
        desc = JSON.parse(product.description);
    } catch (e) {
        desc = { "Mô tả": product.description };
    }

    const formatPrice = (price) => {
        return new Intl.NumberFormat("vi-VN").format(price);
    };

    return (
        <div
            className="relative border border-gray-200 rounded-2xl shadow-md p-4 hover:shadow-lg transition-all duration-300 bg-white max-w-sm mx-auto"
            onMouseEnter={() => showTooltipOnHover && setShowTooltip(true)}
            onMouseLeave={() => showTooltipOnHover && setShowTooltip(false)}
        >
            {/* Product Image */}
            <div className="relative">
                <img
                    src={product.image}
                    alt={product.name}
                    className="w-full h-48 object-cover rounded-xl mb-4"
                />

                {/* Tooltip - Description */}
                {showTooltipOnHover && showTooltip && (
                    <div className="absolute top-0 left-0 right-0 bg-black bg-opacity-90 text-white p-3 rounded-xl z-10 text-sm">
                        <ul className="space-y-1">
                            {Object.entries(desc).map(([key, value]) => (
                                <li key={key} className="flex">
                                    <span className="font-medium text-yellow-300 min-w-max">
                                        {key}:
                                    </span>
                                    <span className="ml-2 text-white">{value}</span>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>

            {/* Product Name */}
            <h3 className="font-bold text-lg text-gray-800 mb-2 line-clamp-2">
                {product.name}
            </h3>

            {/* Quick Info (chỉ hiển thị 2 dòng) */}
            <ul className="text-sm text-gray-600 mb-3 space-y-1">
                {Object.entries(desc)
                    .slice(0, 2)
                    .map(([key, value]) => (
                        <li key={key} className="flex items-center">
                            <span className="w-2 h-2 bg-blue-500 rounded-full mr-2"></span>
                            <span className="font-medium">{key}:</span>
                            <span className="ml-1 truncate">{value}</span>
                        </li>
                    ))}
                {Object.keys(desc).length > 2 && showTooltipOnHover && (
                    <li className="text-blue-500 text-xs font-medium">
                        Hover để xem thêm chi tiết...
                    </li>
                )}
            </ul>

            {/* Price + Button */}
            <div className="border-t pt-3">
                <div className="flex items-center justify-between mb-3">
                    <div>
                        <span className="text-sm text-gray-500">Giá chỉ từ:</span>
                        <div className="text-xl font-bold text-red-500">
                            {formatPrice(product.price)} đ
                        </div>
                    </div>
                </div>

                {/* Nút MUA (dùng component Button tách riêng) */}
                <Button text="MUA TÀI KHOẢN" onClick={() => alert("Mua thành công!")} />
            </div>

            {/* Quantity Badge */}
            {product.quantity && (
                <div className="absolute top-2 right-2 bg-green-500 text-white text-xs px-2 py-1 rounded-full">
                    Còn {product.quantity} sản phẩm
                </div>
            )}
        </div>
    );
};

export default ProductCard;
