import React, { useEffect, useMemo, useState } from "react";
import { useParams } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import SpecList from "../../Components/Product/SpecList";
import QuantityInput from "../../Components/Product/QuantityInput";
import { getTypeProductById, getinfortypeproduct } from "../../Services/ApiTypeProduct";
import Button from "../../Components/Buttons/ButtonBuy";
const parseDescription = (desc) => {
    if (!desc) return {};
    if (typeof desc === "object") return desc;
    try {
        return JSON.parse(desc);
    } catch {
        return {};
    }
};

const formatPrice = (n) =>
    (Number(n) || 0).toLocaleString("vi-VN", { maximumFractionDigits: 0 });

const TypeProductDetail = () => {
    const { id } = useParams();                         // /detail-type-product/:id

    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [qty, setQty] = useState(1);
    const [decribe, setDecribe] = useState()

    useEffect(() => {
        let mounted = true;
        (async () => {
            try {
                setLoading(true);
                const res = await getTypeProductById(id);
                if (mounted) setProduct(res);
            } catch (e) {
                console.error(e);
                if (mounted) setError("Không thể tải chi tiết sản phẩm.");
            } finally {
                if (mounted) setLoading(false);
            }
        })();
        return () => (mounted = false);
    }, [id]);
    useEffect(() => {
        const fetchdata = async () => {
            try {
                const data = await getinfortypeproduct(id);
                setDecribe(data);
            } catch (e) {
                console.error(e);
            }
        };

        fetchdata();  // gọi hàm sau khi định nghĩa
    }, [id]);
    const specs = useMemo(() => parseDescription(product?.description), [product]);

    const handleBuy = () => {
        // TODO: gọi API tạo đơn / mở modal thanh toán tuỳ bạn
        alert(`Mua ${qty} × "${product?.name}" (ID: ${product?.id})`);
    };

    if (loading) {
        return (
            <div className="container py-5 text-center">
                <div className="spinner-border" role="status" />
                <p className="mt-2">Đang tải chi tiết…</p>
            </div>
        );
    }
    if (error) {
        return (
            <div className="container py-5">
                <div className="alert alert-danger text-center">{error}</div>
            </div>
        );
    }
    if (!product) return null;

    return (
        <div className="container py-4">
            <nav className="mb-3 small text-muted">
                <span>TRANG CHỦ</span> / <span>SHOP</span>
            </nav>

            <div className="row g-4">
                {/* Ảnh trái */}
                <div className="col-lg-6">
                    <div className="p-3 border rounded-3 bg-white h-100 d-flex align-items-center justify-content-center">
                        <img
                            src={product.image}
                            alt={product.name}
                            className="img-fluid"
                            style={{ maxHeight: 480, objectFit: "contain" }}
                        />
                    </div>
                </div>

                {/* Thông tin phải */}
                <div className="col-lg-6">
                    <h3 className="fw-bold">{product.name}</h3>

                    <div className="text-success fw-semibold mb-2">
                        Còn sẵn: {product.quantity ?? 0} sản phẩm
                    </div>

                    {/* Giá + giá gốc (mock) */}
                    <div className="d-flex align-items-baseline gap-3 mb-3">
                        <span className="fs-3 fw-bold text-danger">
                            {formatPrice(product.price)} đ
                        </span>
                        <del className="text-muted">{formatPrice(product.price * 2)} đ</del>
                    </div>

                    {/* Thông số dạng chấm đầu dòng */}
                    <SpecList specs={specs} />

                    {/* Chọn số lượng */}
                    <div className="d-flex align-items-center gap-3 my-3">
                        <QuantityInput
                            value={qty}
                            min={1}
                            max={product.quantity ?? 1}
                            onChange={setQty}
                        />
                        <Button
                            text="MUA TÀI KHOẢN"
                            onClick={handleBuy}
                        />
                    </div>

                    {/* Metadata nhỏ */}
                    <div className="mt-3 small text-muted">
                        <div>Mã: HMT{String(product.id).padStart(2, "0")}</div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TypeProductDetail;
