// src/Components/Product/ProductCard.jsx
import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./ProductCard.css";
import Button from "../Buttons/ButtonBuy.jsx";
import ModalPurchaseTypeProduct from "../Modal/ModalPurchase/ModalPurchase.jsx";
import { useNavigate } from "react-router-dom";

const ProductCard = ({ product }) => {
    const navigate = useNavigate();
    const [showModal, setShowModal] = useState(false);

    const handleBuyClick = (e) => {
        e.stopPropagation(); // Ngăn không cho click vào card
        setShowModal(true); // Mở modal
    };

    const handleCloseModal = () => {
        setShowModal(false); // Đóng modal
    };

    const handleViewDetails = () => {
        // Điều hướng đến trang chi tiết sản phẩm
        navigate(`/detail-type-product/${product.id}`);
    };

    // Tự động detect type của description
    let descriptionData = {};
    if (product?.description) {
        if (typeof product.description === "string") {
            try {
                descriptionData = JSON.parse(product.description);
            } catch (e) {
                console.error("Description parse error:", e);
                descriptionData = {};
            }
        } else if (typeof product.description === "object") {
            descriptionData = product.description;
        }
    }

    return (
        <>
            <div className="card product-card shadow-sm h-100" onClick={handleViewDetails} role="button">
                {/* Ảnh sản phẩm */}
                <img
                    src={product?.image}
                    className="card-img-top product-image"
                    alt={product?.name}
                    style={{ height: 200, objectFit: "cover" }}
                />

                {/* Nội dung card */}
                <div className="card-body d-flex flex-column">
                    {/* Tên sản phẩm */}
                    <h6 className="card-title fw-bold text-truncate mb-2">
                        {product?.name}
                    </h6>

                    {/* Giá */}
                    <div className="mt-auto">
                        <div className="d-flex justify-content-between align-items-center mb-2">
                            <span className="text-danger fw-bold fs-5">
                                {product?.price?.toLocaleString("vi-VN") || 0} ₫
                            </span>
                        </div>

                        {/* Hover mô tả */}
                        <div className="card-hover">
                            <ul className="text-start small">
                                {Object.entries(descriptionData).map(([key, value]) => (
                                    <li key={key}>
                                        <strong>{key}:</strong> {value}
                                    </li>
                                ))}
                            </ul>
                        </div>

                        {/* Button */}
                        <Button
                            text="MUA TÀI KHOẢN"
                            onClick={handleBuyClick}
                        />
                    </div>
                </div>
            </div>

            {/* Modal Purchase */}
            <ModalPurchaseTypeProduct
                show={showModal}
                onClose={handleCloseModal}
                id={product?.id}
            />
        </>
    );
};

export default ProductCard;
