import React from "react";
import { NavLink } from "react-router-dom";

import Path from "../../Routes/Paths.jsx";
import { AuthContext } from "../../Context/AuthContext.jsx";
import { useContext } from "react";

const NavBarAccount = [
    { name: "Trang tổng quan", path: Path.OVERVIEW },
    { name: "Đơn hàng của bạn", path: Path.MY_ORDERS },
    { name: "Lịch sử giao dịch", path: Path.TRANSACTION_HISTORY },
    { name: "Nạp tiền", path: Path.RECHARGE },
    { name: "Thay đổi thông tin", path: Path.PAGE_ACCOUNT },
    { name: "Khuyến mãi của bạn", path: Path.PAGE_ACCOUNT },
    { name: "Đăng xuất", path: Path.PAGE_ACCOUNT },

];

const Navbar = () => {
    const { user } = useContext(AuthContext);
    const { funtion, setFuntion } = useContext(AuthContext);

    return (
        <div className="navbar-container">
            <div className="Funtionclick">
                <button onClick={() => setFuntion(!funtion)} className="btn-Funtionclick">

                </button>
            </div>

            <div className="infomation-user">
                <img src={user?.avatar || "https://via.placeholder.com/150"} alt="Avatar" className="avatar" />
                <div className="user-details">

                </div>

                <div className="navbar">
                    {NavBarAccount.map((i) => (    // Duyệt qua từng phần tử trong mảng NavBarAccount
                        <NavLink
                            key={i.path} // Sử dụng path làm key để đảm bảo duy nhất
                            to={i.path} // Đường dẫn đến trang tương ứng
                            className={({ isActive }) =>
                                `navbar-link ${isActive ? "active" : ""}`
                            }
                        >
                            {i.name}
                        </NavLink>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Navbar;
