import React, { useState } from "react";
import Button from "../Buttons/ButtonBuy.jsx";

const ProductCard = ({ product, showTooltipOnHover = false }) => {
    const [showTooltip, setShowTooltip] = useState(false);

    // Parse description JSON string -> object
    let desc = {};
    try {
        desc = JSON.parse(product.description);
    } catch (e) {
        desc = { "Mô tả": product.description };
    }

    const formatPrice = (price) => {
        return new Intl.NumberFormat("vi-VN").format(price);
    };

    return (
        <div>
            <div class Name="card h-100 product-card">
                <img src={product.image} className="card-img-top" alt={product.name} />
                <div className="card-body d-flex flex-column">
                    <h5 className="card-title">{product.name}</h5>
                    <p className="card-text">{desc["Mô tả"]}</p>
                    <div className="mt-auto">
                        <span className="fw-bold">{formatPrice(product.price)} VNĐ</span>
                        <Button />
                    </div>
                </div>
            </div>
        </div>
    );
};
export default ProductCard;
