import * as AuthService from "../Services/AuthService";
import { getProfile } from "../Services/UserService";
import apiClient from "./ApiService";

const login = async (data) => {
  const res = await apiClient.post("/Account/login", data);
  return res.data;
};
const register = async (data) => {
  const res = await apiClient.post("/Account/register", data);
  return res.data;
};

const logout = async () => {
  // nếu backend có endpoint logout, đổi đường dẫn; nếu không, chỉ xoá token ở client
  try {
    await apiClient.post("/Account/logout");
  } catch (e) {
    // ignore if endpoint not present
  }
  localStorage.removeItem("access_token");
  localStorage.removeItem("refresh_token");
  delete apiClient.defaults.headers.common['Authorization'];
};

export default { login, register, logout };
