const TipUseface = () => {
    return (
        <div className="tip-use-face">
            <h1>Hướng dẫn sử dụng Face</h1>
            <p>Face là một nền tảng mạng xã hội phổ biến, dưới đây là hướng dẫn chi tiết về cách sử dụng Face hiệu quả.</p>
            <ul>
                <li>Bước 1: Tạo tài khoản Face bằng email hoặc số điện thoại.</li>
                <li>Bước 2: Cập nhật thông tin cá nhân và ảnh đại diện.</li>
                <li>Bước 3: Kết nối với bạn bè và tham gia các nhóm thú vị.</li>
            </ul>
        </div>
    );
}
export default TipUseface;
