import React, { useState, useEffect } from 'react';
import { Alert } from 'react-bootstrap';
import { BASE_URL } from '../../Services/BaseURL';

const ConnectionStatus = () => {
  const [status, setStatus] = useState('checking');
  const [error, setError] = useState(null);

  useEffect(() => {
    const checkConnection = async () => {
      try {
        const response = await fetch(`${BASE_URL}/docs`, {
          method: 'HEAD',
          mode: 'cors'
        });
        
        if (response.ok) {
          setStatus('connected');
          setError(null);
        } else {
          setStatus('error');
          setError(`Server response: ${response.status}`);
        }
      } catch (err) {
        setStatus('error');
        setError(`Connection failed: ${err.message}`);
      }
    };

    checkConnection();
    
    // Check every 30 seconds
    const interval = setInterval(checkConnection, 30000);
    
    return () => clearInterval(interval);
  }, []);

  // Ch·ªâ hi·ªÉn th·ªã trong development mode
  if (process.env.NODE_ENV !== 'development') {
    return null;
  }

  const getAlertVariant = () => {
    switch (status) {
      case 'connected': return 'success';
      case 'error': return 'danger';
      default: return 'info';
    }
  };

  const getMessage = () => {
    switch (status) {
      case 'connected': return `‚úÖ K·∫øt n·ªëi API th√†nh c√¥ng: ${BASE_URL}`;
      case 'error': return `‚ùå L·ªói k·∫øt n·ªëi API: ${error}`;
      default: return `üîÑ ƒêang ki·ªÉm tra k·∫øt n·ªëi API...`;
    }
  };

  // Safe access to window object
  const currentUrl = typeof window !== 'undefined' ? window.location.href : 'Unknown';
  const hostname = typeof window !== 'undefined' ? window.location.hostname : 'Unknown';

  return (
    <Alert variant={getAlertVariant()} className="mb-2" style={{ fontSize: '0.8rem' }}>
      <strong>Debug Info:</strong> {getMessage()}
      <br />
      <small>
        Current URL: {currentUrl} | 
        API Base: {BASE_URL} |
        Hostname: {hostname}
      </small>
    </Alert>
  );
};

export default ConnectionStatus;
