import './HomePage.css';
import Logo from '../../Assets/Logo/Logo.jsx';
import React, { useState, useEffect } from "react";
import ProductCard from '../../Components/Card/ProductCard.jsx';
import { getAllProductswithcategory } from '../../Services/ApiTypeProduct.jsx';

const HomePage = () => {
    const [product, setProduct] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const data = await getAllProductswithcategory();
                setProduct(data);
                console.log(data);
            } catch (error) {
                console.error("Error fetching products with category:", error);
            }
        };
        fetchProducts();
    }, []);

    return (
        <div className="homepage">
            <div className="container">
                <div className="row g-2">
                    {/* Banner bên trái */}
                    <div className="col-12">
                        <div className="banner-image">
                            <img src={Logo.Banner} alt="Vuavia Banner" className="img-fluid" />
                        </div>
                    </div>
                </div>
            </div>

            {/* Phần hiển thị sản phẩm theo 4 cột */}
            <div className="container mt-4">
                <h2 className="text-center mb-4">Danh Sách Sản Phẩm</h2>
                <div className="row g-3">
                    {product && product.length > 0 ? (
                        product.map((item) => (
                            <div key={item.id} className="col-lg-3 col-md-4 col-sm-6 col-12 mb-4">
                                <ProductCard product={item} />
                            </div>
                        ))
                    ) : (
                        <p className="text-center">Đang tải sản phẩm...</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default HomePage;
