import React, { useContext } from "react";
import Logo from "../../Assets/Logo/Logo";
import { FiSearch } from "react-icons/fi";
import "./Header.css";
import { AuthContext } from "../../Context/AuthContext";
import { useNavigate, Link } from "react-router-dom";

const Header = () => {
    const { user, logout } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleClickLogo = () => {
        navigate("/");
    };

    return (
        <header className="header">
            {/* Logo */}
            <div className="logo-container" onClick={handleClickLogo}>
                <img src={Logo.ImgLogo} alt="Logo" className="logo" />
            </div>

            {/* Search */}
            <div className="search-container">
                <input type="text" className="search-input" placeholder="Tìm sản phẩm" />
                <FiSearch className="search-icon" />
            </div>

            {/* Auth */}
            <div className="auth-container">
                {!user ? (
                    <>
                        <span className="auth-link">Đăng nhập</span>
                        {" / "}
                        <Link to="/register" className="auth-link">Đăng ký</Link>
                    </>
                ) : (
                    <div className="dropdown">
                        <div className="dropbtn">
                            <img
                                src={user.avatar || "/default-avatar.png"}
                                alt="avatar"
                                className="avatar"
                            />
                            <span className="username">{user.username}</span>
                        </div>
                        <div className="dropdown-content">
                            <Link to="/profile">Trang Tài Khoản</Link>
                            <Link to="/orders">Đơn Hàng</Link>
                            <Link to="/wallet">Lịch Sử Giao Dịch</Link>
                            <span onClick={logout}>Đăng Xuất</span>
                        </div>
                    </div>
                )}
            </div>
        </header>
    );
};

export default Header;
