import React from "react";
import { Card, ListGroup } from "react-bootstrap";
import { AuthContext } from "../../Context/AuthContext";

const MyAccount = () => {
    const { user, logout } = React.useContext(AuthContext);

    return (
        <div className="d-flex my-4" style={{ minHeight: "80vh" }}>
            {/* Sidebar */}
            <div
                className="shadow-sm p-3 text-center"
                style={{ width: "250px", flexShrink: 0 }}
            >
                {/* Avatar */}
                <img
                    src={user.avatar}
                    alt="Avatar"
                    className="rounded-circle"
                    style={{ width: "100px", height: "100px" }}
                />
                {/* Name + Email */}
                <h6 className="mt-3 mb-0">{user.name}</h6>
                <small className="text-muted">{user.email}</small>

                {/* Menu */}
                <ListGroup className="mt-4" variant="flush">
                    <ListGroup.Item action active>
                        Trang tổng quan
                    </ListGroup.Item>
                    <ListGroup.Item action>Đơn hàng của bạn</ListGroup.Item>
                    <ListGroup.Item action>Lịch sử giao dịch</ListGroup.Item>
                    <ListGroup.Item action>Nạp tiền</ListGroup.Item>
                    <ListGroup.Item action>Thay đổi thông tin tài khoản</ListGroup.Item>
                    <ListGroup.Item action>Khuyến mãi của bạn</ListGroup.Item>
                    <ListGroup.Item action onClick={logout}>
                        Đăng xuất
                    </ListGroup.Item>
                </ListGroup>
            </div>

            {/* Main Content */}
            <div className="flex-grow-1 p-3">
                <Card className="shadow-sm border-0">
                    <Card.Body>
                        <Card.Title>Tổng quan tài khoản</Card.Title>
                        <Card.Text>
                            Chào mừng bạn đến với trang tổng quan tài khoản của chúng tôi.
                        </Card.Text>
                    </Card.Body>
                </Card>
            </div>
        </div>
    );
};

export default MyAccount;
