import React, { useState, useContext, useEffect } from "react";
import Logo from "../../Assets/Logo/Logo";
import { FiSearch } from "react-icons/fi";
import "./Header.css";
import ModalLogin from "../Modal/ModalLogin/ModalLogin";
import { AuthContext } from "../../Context/AuthContext";
import { useNavigate, Link } from "react-router-dom";
import { Navbar, Container, Nav, NavDropdown } from "react-bootstrap";
import DefaultAvt from "../../Assets/Logo/Logo";

const Header = () => {
  const [showModal, setShowModal] = useState(false);
  const { user, logout } = useContext(AuthContext);
  const [balance, setBalance] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    setBalance(user?.balance || 0);
  }, [user]);

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  return (
    <>
      <Navbar expand="lg" bg="light" className="shadow-sm">
        <Container>
          {/* Logo */}
          <Navbar.Brand as={Link} to="/">
            <img
              src={Logo.ImgLogo}
              alt="logo"
              style={{ height: "40px", cursor: "pointer" }}
            />
          </Navbar.Brand>

          {/* Search */}
          <div className="search-container">
            <input
              type="text"
              className="search-input"
              placeholder="Tìm sản phẩm"
            />
            <FiSearch className="search-icon" />
          </div>

          {/* Auth / User */}
          <Nav>
            {!user ? (
              <span
                style={{ color: "#f57c00", fontWeight: "600", cursor: "pointer" }}
                onClick={() => setShowModal(true)}
              >
                Đăng nhập / Đăng ký
              </span>
            ) : (
              <NavDropdown
                title={
                  <span>
                    <img
                      src={user.avatar || DefaultAvt}
                      alt="avatar"
                      style={{
                        width: "30px",
                        height: "30px",
                        borderRadius: "50%",
                        marginRight: "6px",
                        border: "2px solid #f57c00",
                      }}
                    />
                    {user.surname + " " + user.name}
                  </span>
                }
                id="user-dropdown"
                align="end"
              >
                <NavDropdown.Item as={Link} to="/account">
                  Trang tài khoản
                </NavDropdown.Item>
                <NavDropdown.Item as={Link} to="/my-orders">
                  Đơn hàng của tôi
                </NavDropdown.Item>
                <NavDropdown.Item as={Link} to="/recharge">
                  Nạp tiền
                </NavDropdown.Item>
                <NavDropdown.Item as={Link} to="/transaction-history">
                  Lịch sử giao dịch
                </NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item onClick={handleLogout}>
                  Đăng xuất
                </NavDropdown.Item>
              </NavDropdown>
            )}
          </Nav>

          {user && (
            <div className="balance-container ms-3">
              <button className="balance-button">Số dư: {balance} đ</button>
            </div>
          )}
        </Container>
      </Navbar>

      {/* Modal Login */}
      <ModalLogin show={showModal} handleClose={() => setShowModal(false)} />
    </>
  );
};

export default Header;



