import React, { useState, useContext, useEffect } from 'react';
import Logo from '../../Assets/Logo/Logo';
import { FiSearch } from 'react-icons/fi';
import './Header.css';
import ModalLogin from '../Modal/ModalLogin/ModalLogin';
import { AuthContext } from '../../Context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';
import DefaultAvt from '../../Assets/Logo/Logo';
import MenuDropDown from '../Menu/Menu';

const Header = () => {
    const [showModal, setShowModal] = useState(false);
    const { user, logout } = useContext(AuthContext);
    const [balance, setBalance] = useState(0);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const navigate = useNavigate();

    const handleClickLogo = () => {
        navigate('/');
    };

    const handleLoginClick = () => {
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    useEffect(() => {
        if (user?.balance) {
            setBalance(user.balance);
        } else {
            setBalance(0);
        }
    }, [user]);

    return (
        <>
            <header className="header">
                {/* Logo */}
                <div className="logo-container">
                    <img
                        src={Logo.ImgLogo}
                        alt="VUA Logo"
                        className="logo"
                        onClick={handleClickLogo}
                    />
                </div>

                {/* Search */}
                <div className="search-container">
                    <input
                        type="text"
                        className="search-input"
                        placeholder="Tìm sản phẩm"
                    />
                    <FiSearch className="search-icon" />
                </div>

                {/* Auth */}
                <div className="auth-container">
                    {!user ? (
                        <>
                            <span onClick={handleLoginClick}>Đăng nhập</span>
                            {" / "}
                            <Link to="/register">Đăng ký</Link>
                        </>
                    ) : (
                        <div className="user-menu">
                            <div
                                className="user-info"
                                onMouseEnter={() => setIsDropdownOpen(true)}
                                onMouseLeave={() => setIsDropdownOpen(false)}
                            >
                                <img
                                    src={user.avatar || DefaultAvt}
                                    alt="avatar"
                                    className="avatar"
                                />
                                <span>{user.surname + " " + user.name}</span>

                                <div className="dropdown-wrapper">
                                    {isDropdownOpen && (
                                        <MenuDropDown onLogout={logout} />
                                    )}
                                </div>
                            </div>

                            <div className="balance-container">
                                <button className="balance-button">
                                    SỐ DƯ: {balance} đ
                                </button>
                            </div>
                        </div>
                    )}
                </div>
            </header>

            {/* Modal Login */}
            <ModalLogin show={showModal} handleClose={handleCloseModal} />
        </>
    );
};

export default Header;

