import { Container, Row, Col, Button } from "react-bootstrap";
import "./AccViet.css";
import React, { useState, useEffect } from "react";
import Logo from "../../Assets/Logo/Logo";
import ProductCard from "../../Components/Card/ProductCard.jsx";
import { getListtypeproductByCateGory } from "../../Services/ApiTypeProduct.jsx"


const AccViet = () => {
    const [dataTypeProduct, setDataTypeProduct] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await getListtypeproductByCateGory(3);
                setDataTypeProduct(data);
                console.log("Sản phẩm đã tải:", data);
            } catch (error) {
                console.error("Lỗi khi tải sản phẩm:", error);
            }
        };

        fetchData();
    }, []);
    return (
        <div className="accviet">
            <div className="banner-section">
                <Container>
                    <Row className="align-items-center">
                        {/* Left text */}
                        <Col md={6}>
                            <h2 className="fw-bold text-primary">
                                MUA VIA VIỆT 2FA, XMDT, VIỆT CỔ, VIỆT LIMIT 5M8
                            </h2>
                            <p className="mt-3">
                                Chào mừng bạn đến với <strong>VuaVia.VN</strong>. Chúng tôi là một đơn vị cung cấp via việt
                                các loại uy tín, lâu năm, có nhiều kinh nghiệm trong vấn đề về Via.
                                Vua Via là địa chỉ mua via việt xmdt, via việt cổ, via việt limit 5m8, 32m giá rẻ.
                                Tài khoản Via Việt được giao có đầy đủ Email bảo mật, 2FA đảm bảo an toàn cho khách hàng.
                            </p>
                            <div className="mt-4">
                                <Button variant="outline-danger" className="me-3">
                                    MUA NGAY
                                </Button>
                                <Button variant="outline-danger">CẦN TƯ VẤN</Button>
                            </div>
                        </Col>

                        {/* Right image */}
                        <Col md={6} className="text-center">
                            <img
                                src={Logo.LogoAccViet}
                                alt="Facebook 3D"
                                className="img-fluid"
                                style={{ maxWidth: "400px" }}
                            />
                        </Col>
                    </Row>
                </Container>
            </div>
            <div className="content-section">
                <h2 className='category-title fw-bold mb-2'>Via Việt chuyên Dùng Chạy Quảng Cáo Facebook</h2>
                <hr style={{ border: "2px solid #F39C12", width: "50%", margin: "0 auto" }} />
            </div>
            {dataTypeProduct.map((type_products) => (
                <div key={type_products.id} className="mb-5">
                    <h4 className="category-title fw-bold mb-2 text-warning">
                        {type_products.name}
                    </h4>

                    <div className="row row-cols-1 row-cols-md-4 g-2">
                        {type_products.products.map((product) => (
                            <div key={product.id} className="col">
                                <ProductCard product={product} />
                            </div>
                        ))}
                    </div>
                </div>
            ))}
        </div>

    )
}

export default AccViet;
