import React, { useState } from "react";
import Button from "../Buttons/ButtonBuy.jsx";

const ProductCard = ({ product, showTooltipOnHover = false }) => {
    const [showTooltip, setShowTooltip] = useState(false);

    // Parse description JSON string -> object
    let desc = {};
    try {
        desc = JSON.parse(product.description);
    } catch (e) {
        desc = { "Mô tả": product.description };
    }

    const formatPrice = (price) => {
        return new Intl.NumberFormat("vi-VN").format(price);
    };

    return (
        <div
            className="relative border border-gray-200 rounded-2xl shadow-md p-4 hover:shadow-lg transition-all duration-300 bg-white max-w-sm mx-auto overflow-hidden"
            onMouseEnter={() => showTooltipOnHover && setShowTooltip(true)}
            onMouseLeave={() => showTooltipOnHover && setShowTooltip(false)}
        >
            {/* Product Image */}
            <div className="relative group">
                <img
                    src={product.image}
                    alt={product.name}
                    className="w-full h-48 object-cover rounded-xl mb-4 transition-transform duration-300 group-hover:scale-105"
                />

                {/* Tooltip - Description */}
                {showTooltipOnHover && showTooltip && (
                    <div className="absolute inset-0 bg-black bg-opacity-80 text-white p-4 rounded-xl z-10 flex items-center justify-center transition-opacity duration-300">
                        <div className="max-h-full overflow-y-auto">
                            <ul className="space-y-2 text-sm">
                                {Object.entries(desc).map(([key, value]) => (
                                    <li key={key} className="flex flex-col sm:flex-row">
                                        <span className="font-semibold text-yellow-300 min-w-max mb-1 sm:mb-0 sm:mr-2">
                                            {key}:
                                        </span>
                                        <span className="text-white leading-relaxed">{value}</span>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                )}
            </div>

            {/* Product Name */}
            <h3 className="font-bold text-lg text-gray-800 mb-3 line-clamp-2 text-center">
                {product.name}
            </h3>

            {/* Quick Info (chỉ hiển thị 2 dòng) */}
            <ul className="text-sm text-gray-600 mb-4 space-y-2">
                {Object.entries(desc)
                    .slice(0, 2)
                    .map(([key, value]) => (
                        <li key={key} className="flex items-start">
                            <span className="w-2 h-2 bg-blue-500 rounded-full mr-3 mt-1 flex-shrink-0"></span>
                            <div className="flex-1">
                                <span className="font-medium">{key}:</span>
                                <span className="ml-1 text-gray-700">{value}</span>
                            </div>
                        </li>
                    ))}
                {Object.keys(desc).length > 2 && showTooltipOnHover && (
                    <li className="text-blue-500 text-xs font-medium text-center mt-2">
                        Hover để xem thêm chi tiết...
                    </li>
                )}
            </ul>

            {/* Price + Button */}
            <div className="border-t pt-4">
                <div className="flex items-center justify-between mb-4">
                    <div className="text-center flex-1">
                        <span className="text-sm text-gray-500 block">Giá chỉ từ:</span>
                        <div className="text-2xl font-bold text-red-500">
                            {formatPrice(product.price)} đ
                        </div>
                    </div>
                </div>

                {/* Nút MUA (dùng component Button tách riêng) */}
                <div className="text-center">
                    <Button text="MUA TÀI KHOẢN" onClick={() => alert("Mua thành công!")} />
                </div>
            </div>

            {/* Quantity Badge */}
            {product.quantity && (
                <div className="absolute top-3 right-3 bg-green-500 text-white text-xs px-3 py-1 rounded-full shadow-md">
                    Còn {product.quantity} sản phẩm
                </div>
            )}
        </div>
    );
};

export default ProductCard;
