import React, { useState, useEffect } from 'react';
import './HomePage.css';
import Logo from '../../Assets/Logo/Logo.jsx';
import { NavLink } from 'react-router-dom';
import ProductCard from '../../Components/Card/ProductCard.jsx';
import { getAllProductswithcategory } from '../../Services/ApiTypeProduct.jsx';

const HomePage = () => {
    const latestPosts = [
        {
            id: 1,
            title: "Hướng Dẫn Cài Proxy Cho Telegram Miễn Phí - Truy Cập Nhanh, Bỏ Chặn Dễ Dàng",
            image: Logo.share1,
            navlink: "/proxy-tutorial"
        },
        {
            id: 2,
            title: "Các tips và chiến lược để tối ưu hóa việc đăng bài Facebook theo khung giờ.",
            image: Logo.share2,
            navlink: "/tips"
        },
        {
            id: 3,
            title: "Mẹo để sử dụng Facebook Lead Ads thu thập thông tin khách hàng tiềm năng",
            image: Logo.share3,
            navlink: "tip-create-ads"
        },
        {
            id: 4,
            title: "Mẹo tạo quảng cáo story trên facebook hiệu quả và thu hút người xem",
            image: Logo.share4,
            navlink: "/tip-use-face"
        }
    ];

    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await getAllProductswithcategory();
                // API trả về có thể là { type_products: [...] }
                setProducts(data?.type_products || []);
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };
        fetchData();
    }, []);

    return (
        <div className="homepage">
            <div className="container">
                {/* Hàng banner + sidebar */}
                <div className="row g-2 mb-4">
                    {/* Banner bên trái */}
                    <div className="col-md-8">
                        <div className="banner-image">
                            <img src={Logo.Banner} alt="Vuavia Banner" className="img-fluid rounded" />
                        </div>
                    </div>

                    {/* Sidebar bên phải */}
                    <div className="col-md-4">
                        <div className="sidebar-container p-3 border rounded">
                            <div className="sidebar-header d-flex align-items-center mb-2">
                                <span className="dropdown-icon me-2">▼</span>
                                <span className="sidebar-title fw-bold">CHIA SẺ MỚI NHẤT !</span>
                            </div>
                            <div className="sidebar-posts">
                                {latestPosts.map((post) => (
                                    <div key={post.id} className="d-flex mb-3">
                                        <div className="me-2" style={{ width: "60px" }}>
                                            <img src={post.image} alt={post.title} className="img-fluid rounded" />
                                        </div>
                                        <div className="flex-grow-1">
                                            <h6 className="mb-0">
                                                <NavLink to={post.navlink} className="text-decoration-none">
                                                    {post.title}
                                                </NavLink>
                                            </h6>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>

                {/* Danh mục sản phẩm */}
                <h4 className="mb-3">Danh mục sản phẩm</h4>
                <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-4">
                    {products.map((p) => (
                        <ProductCard key={p.id} product={p} />
                    ))}
                </div>
            </div>
        </div>
    );
};

export default HomePage;
