import React from "react";
import { Container, Row, Col, Card, Button, ListGroup } from "react-bootstrap";
import { AuthContext } from '../../Context/AuthContext';


const MyAccount = () => {
    const { user, logout } = React.useContext(AuthContext);
    return (
        <Container className="my-4">
            <Row>
                {/* Sidebar */}
                <Col md={3}>
                    <Card className="shadow-sm p-3   border-0 text-center">
                        <img src={user.avatar} alt="Avatar" className="rounded-circle" style={{ width: "100px", height: "100px" }}
                        />
                        <h6 className="mt-3 mb-0">{user.name}</h6>
                        <small className="text-muted">
                            {user.email}
                        </small>


                        <ListGroup className="mt-4" variant="flush">
                            <ListGroup.Item action active>
                                Trang tổng quan
                            </ListGroup.Item>
                            <ListGroup.Item action>Đơn hàng của bạn</ListGroup.Item>
                            <ListGroup.Item action>Lịch sử giao dịch</ListGroup.Item>
                            <ListGroup.Item action>Nạp tiền</ListGroup.Item>
                            <ListGroup.Item action>Thay đổi thông tin tài khoản</ListGroup.Item>
                            <ListGroup.Item action>Khuyến mãi của bạn</ListGroup.Item>
                            <ListGroup.Item action>Đăng xuất</ListGroup.Item>
                        </ListGroup>
                    </Card>
                </Col>

                {/* Main content */}
                <Col md={9}>
                    <Card className="shadow-sm p-3 border-0">
                        <Card.Body>
                            <Card.Title>Tổng quan tài khoản</Card.Title>
                            <Card.Text>
                                Chào mừng bạn đến với trang tổng quan tài khoản của chúng tôi.
                            </Card.Text>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
};

export default MyAccount;
