import React from "react";
import { NavLink } from "react-router-dom";
import { useState } from "react";

import Path from "../../Routes/Paths.jsx";
import { AuthContext } from "../../Context/AuthContext.jsx";
import { useContext } from "react";
import "./PageAccount.css"; // Import CSS file

const NavBarAccount = [
    { 
        name: "Trang t·ªïng quan", 
        path: Path.OVERVIEW,
        icon: "üìä"
    },
    { 
        name: "ƒê∆°n h√†ng c·ªßa b·∫°n", 
        path: Path.MY_ORDERS,
        icon: "üì¶"
    },
    { 
        name: "L·ªãch s·ª≠ giao d·ªãch", 
        path: Path.TRANSACTION_HISTORY,
        icon: "üí≥"
    },
    { 
        name: "N·∫°p ti·ªÅn", 
        path: Path.RECHARGE,
        icon: "üí∞"
    },
    { 
        name: "Thay ƒë·ªïi th√¥ng tin", 
        path: Path.PAGE_ACCOUNT,
        icon: "‚öôÔ∏è"
    },
    { 
        name: "Khuy·∫øn m√£i c·ªßa b·∫°n", 
        path: Path.PAGE_ACCOUNT,
        icon: "üéÅ"
    },
    { 
        name: "ƒêƒÉng xu·∫•t", 
        path: Path.PAGE_ACCOUNT,
        icon: "üö™"
    },
];

const Navbar = () => {
    const { user } = useContext(AuthContext);
    const { funtion, setFuntion } = useContext(AuthContext);
    const [showMenu, setShowMenu] = useState(false);

    const toggleMenu = () => {
        setShowMenu(!showMenu);
        setFuntion(!funtion);
    };

    const handleLogout = () => {
        // Logic ƒëƒÉng xu·∫•t
        localStorage.removeItem("access_token");
        localStorage.removeItem("refresh_token");
        window.location.href = Path.LOGIN;
    };

    return (
        <div className="navbar-container">
            {/* Header v·ªõi button menu */}
            <div className="navbar-header">
                <div className="user-info-header">
                    <img 
                        src={user?.avatar || "https://via.placeholder.com/40"} 
                        alt="Avatar" 
                        className="avatar-small" 
                    />
                    <div className="user-name">
                        <span>Xin ch√†o, {user?.name || "Nh·∫≠t Minh Tr·∫ßn"}</span>
                        <small>ƒê√¢y l√† trang t·ªïng quan c·ªßa Vuavia.VN</small>
                    </div>
                </div>
                
                <div className="menu-toggle">
                    <button 
                        onClick={toggleMenu} 
                        className={`btn-menu-toggle ${showMenu ? 'active' : ''}`}
                        aria-label="Toggle menu"
                    >
                        <span className="hamburger-line"></span>
                        <span className="hamburger-line"></span>
                        <span className="hamburger-line"></span>
                    </button>
                </div>
            </div>

            {/* Menu dropdown */}
            <div className={`navbar-menu ${showMenu ? 'show' : ''}`}>
                <div className="user-profile">
                    <img 
                        src={user?.avatar || "https://via.placeholder.com/80"} 
                        alt="Avatar" 
                        className="avatar-large" 
                    />
                    <div className="user-details">
                        <h3>{user?.name || "Nh·∫≠t Minh Tr·∫ßn"}</h3>
                        <p>{user?.email || "minhluhanfmessi@gmail.com"}</p>
                        <span className="user-balance">S·ªë d∆∞: {user?.balance || "0"} VNƒê</span>
                    </div>
                </div>

                <div className="navbar-links">
                    {NavBarAccount.map((item, index) => (
                        <div key={index} className="navbar-item">
                            {item.name === "ƒêƒÉng xu·∫•t" ? (
                                <button 
                                    onClick={handleLogout}
                                    className="navbar-link logout-btn"
                                >
                                    <span className="icon">{item.icon}</span>
                                    <span className="text">{item.name}</span>
                                </button>
                            ) : (
                                <NavLink
                                    to={item.path}
                                    className={({ isActive }) =>
                                        `navbar-link ${isActive ? "active" : ""}`
                                    }
                                    onClick={() => setShowMenu(false)}
                                >
                                    <span className="icon">{item.icon}</span>
                                    <span className="text">{item.name}</span>
                                </NavLink>
                            )}
                        </div>
                    ))}
                </div>
            </div>

            {/* Overlay ƒë·ªÉ ƒë√≥ng menu khi click b√™n ngo√†i */}
            {showMenu && (
                <div 
                    className="navbar-overlay" 
                    onClick={() => setShowMenu(false)}
                ></div>
            )}
        </div>
    );
};

export default Navbar;
