import React, { useState, useContext, useEffect } from 'react';
import Logo from '../../Assets/Logo/Logo';
import { FiSearch } from 'react-icons/fi';
import { AuthContext } from '../../Context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [showModal, setShowModal] = useState(false);
  const { user, logout } = useContext(AuthContext);
  const [balance, setBalance] = useState(0);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (user?.balance) setBalance(user.balance);
    else setBalance(0);
  }, [user]);

  return (
    <header className="header">
      {/* Logo */}
      <div className="logo-container" onClick={() => navigate('/')}>
        <img src={Logo.ImgLogo} alt="Logo" className="logo" />
      </div>

      {/* Search */}
      <div className="search-container">
        <input type="text" placeholder="Tìm sản phẩm" className="search-input" />
        <FiSearch className="search-icon" />
      </div>

      {/* Auth */}
      <div className="auth-container">
        {!user ? (
          <>
            <span onClick={() => setShowModal(true)} className="auth-link">
              Đăng nhập
            </span>
            {" / "}
            <Link to="/register" className="auth-link">
              Đăng ký
            </Link>
          </>
        ) : (
          <div className="user-menu">
            {/* User Info */}
            <div 
              className="user-info" 
              onClick={() => setIsDropdownOpen(!isDropdownOpen)}
            >
              <img 
                src={user.avatar || "/default-avatar.png"} 
                alt="avatar" 
                className="avatar" 
              />
              <span className="username">{user.surname + ' ' + user.name}</span>
            </div>

            {/* Dropdown */}
            {isDropdownOpen && (
              <div className="dropdown-content">
                <span onClick={() => navigate('/profile')}>Trang cá nhân</span>
                <span onClick={logout}>Đăng xuất</span>
              </div>
            )}

            {/* Balance */}
            <div className="balance-container">
              <button className="balance-button">SỐ DƯ: {balance} đ</button>
            </div>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
