// Cấu hình Base URL động dựa trên môi trường
const getBaseURL = () => {
  // Sử dụng environment variable nếu có
  if (process.env.REACT_APP_API_URL && process.env.REACT_APP_API_URL !== 'auto') {
    return process.env.REACT_APP_API_URL;
  }
  
  // Kiểm tra nếu đang chạy trong browser
  if (typeof window !== 'undefined') {
    // Tự động phát hiện host hiện tại
    const currentHost = window.location.hostname;
    
    // Nếu truy cập từ localhost hoặc 127.0.0.1
    if (currentHost === 'localhost' || currentHost === '127.0.0.1') {
      return 'http://localhost:8000';
    }
    
    // Nếu truy cập từ IP mạng, sử dụng IP backend mặc định
    return 'http://192.168.1.67:8000';
  }
  
  // Fallback cho server-side rendering
  return 'http://192.168.1.67:8000';
};

export const BASE_URL = getBaseURL();

// Debug information (chỉ trong development và browser)
if (process.env.NODE_ENV === 'development' && typeof window !== 'undefined') {
  console.log('Current hostname:', window.location.hostname);
  console.log('Base URL được sử dụng:', BASE_URL);
}
